import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/login/login', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      // Extract the token and user details from the response
      const { token, userID, name } = response.data;

      // Store the token and user details in localStorage
      //localStorage.setItem('authToken', token);
      //to ensure that when i resatrts i must reenter the login
      localStorage.setItem('tokenExpiration', Date.now() + 3600 * 1000); // 1 hour in milliseconds
      sessionStorage.setItem('authToken', token); 
      sessionStorage.setItem('tokenExpiration', expirationTime);


      localStorage.setItem('userID', userID); // Store userID correctly
      localStorage.setItem('name', name); // Save full name
      console.log(response.data);

      // Redirect to the home page or any other page after successful login
      navigate('/'); // Change this to the desired path after login

    } catch (error) {
      console.error('Error logging in:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800">Login</h1>
      <form onSubmit={handleSubmit} className="mt-4">
        <div>
          <label className="block text-gray-700">Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="border rounded p-2 w-full"
          />
        </div>
        <div className="mt-4">
          <label className="block text-gray-700">Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            className="border rounded p-2 w-full"
          />
        </div>
        <button type="submit" className="mt-4 bg-blue-500 text-white p-2 rounded">
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
