import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ProfilePage() {
  const [name, setName] = useState('');
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState({
    houseNumber: '',
    area: '',
    city: '',
    state: '',
    pincode: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the name of the user on component mount
    const token = localStorage.getItem('authToken');
    fetch('/api/get-user', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(response => response.json())
      .then(data => {
        if (data.name) {
          setName(data.name);
        } else {
          // Handle case when user isn't authenticated
          navigate('/login');
        }
      })
      .catch(error => console.error('Error fetching user info:', error));
  }, [navigate]);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setAddress((prevAddress) => ({
      ...prevAddress,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('image', image);
    formData.append('houseNumber', address.houseNumber);
    formData.append('area', address.area);
    formData.append('city', address.city);
    formData.append('state', address.state);
    formData.append('pincode', address.pincode);

    const token = localStorage.getItem('authToken');
    try {
      const response = await fetch('/api/tasker-profile', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert('Profile updated successfully');
      } else {
        alert(`Error: ${data.message}`);
      }
    } catch (error) {
      console.error('Error saving profile:', error);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold">Tasker Profile</h1>
      <form onSubmit={handleSubmit} className="mt-6">
        <div>
          <label className="block font-semibold">Name:</label>
          <p>{name}</p>
        </div>
        <div className="mt-4">
          <label className="block font-semibold">Profile Image:</label>
          <input type="file" onChange={handleImageChange} />
        </div>
        <div className="mt-4">
          <label className="block font-semibold">House Number:</label>
          <input
            type="text"
            name="houseNumber"
            value={address.houseNumber}
            onChange={handleAddressChange}
            className="border p-2 rounded"
          />
        </div>
        <div className="mt-4">
          <label className="block font-semibold">Area:</label>
          <input
            type="text"
            name="area"
            value={address.area}
            onChange={handleAddressChange}
            className="border p-2 rounded"
          />
        </div>
        <div className="mt-4">
          <label className="block font-semibold">City:</label>
          <input
            type="text"
            name="city"
            value={address.city}
            onChange={handleAddressChange}
            className="border p-2 rounded"
          />
        </div>
        <div className="mt-4">
          <label className="block font-semibold">State:</label>
          <input
            type="text"
            name="state"
            value={address.state}
            onChange={handleAddressChange}
            className="border p-2 rounded"
          />
        </div>
        <div className="mt-4">
          <label className="block font-semibold">Pincode:</label>
          <input
            type="text"
            name="pincode"
            value={address.pincode}
            onChange={handleAddressChange}
            className="border p-2 rounded"
          />
        </div>
        <button
          type="submit"
          className="mt-6 bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Save Profile
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
