import { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment2 = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { remaining_Amount, bookingId } = location.state || {}; // Get the remaining amount and booking ID from passed state

  const [remaining, setRemaining] = useState(remainingAmount || 0);

  useEffect(() => {
    if (!remainingAmount) {
      alert('No remaining amount passed. Redirecting to booking page...');
      navigate('/booking');  // Redirect if no amount is passed
    }
  }, [remainingAmount, navigate]);

  // Function to handle the "Pay" button click
  const handlePay = async () => {
    try {
      // Make an API call to update the payment status in the backend
      const response = await axios.post('http://localhost:5000/api/update-payment', {
        bookingId,
        fullAmountPaid: true  // Indicate that the remaining amount should be marked as paid
      });

      if (response.data.success) {
        alert('Payment successful! Booking updated.');
        navigate('/Mybooking');  // Redirect to the bookings page after successful payment
      } else {
        alert('Failed to update booking status.');
      }
    } catch (error) {
      console.error('Error updating payment:', error);
      alert('There was an error processing your payment.');
    }
  };

  return (
    <div className="bg-gradient-to-r from-gray-800 via-gray-900 to-black min-h-screen flex items-center justify-center py-12 px-6">
      <div className="max-w-7xl w-full flex bg-white rounded-xl shadow-lg overflow-hidden">
        <div className="w-full lg:w-1/2 px-6 py-8 bg-gray-50">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Remaining Payment</h2>
          <p className="text-lg text-gray-800">Remaining Amount: â‚¹{remaining}</p>
          <button
            onClick={handlePay}  // Trigger payment update on click
            className="mt-4 px-8 py-4 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 transition"
          >
            Pay Remaining Amount
          </button>
          <button
            onClick={() => navigate('/Mybooking')}  // Navigate back to booking page
            className="mt-4 px-8 py-4 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 transition"
          >
            Back to Booking
          </button>
        </div>
      </div>
    </div>
  );
};

export default Payment2;
