// CancellationPage.js
import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function CancellationPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { bookingId } = location.state || {}; // Get bookingId from state
  const [cancellationDetails, setCancellationDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch booking details
    const fetchBookingDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/getBookingDetails?bookingId=${bookingId}`);
        setCancellationDetails(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching booking details:', error);
        setLoading(false);
      }
    };

    if (bookingId) fetchBookingDetails();
  }, [bookingId]);

  const handleConfirmCancellation = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/cancel-booking', { bookingId });
      if (response.data.success) {
        alert('Booking cancelled successfully');
        navigate('/my-bookings'); // Redirect to MyBookings page after cancellation
      } else {
        alert('Failed to cancel booking');
      }
    } catch (error) {
      console.error('Error cancelling booking:', error);
      alert('Error cancelling booking');
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-4">Cancellation Policy</h1>
      {loading ? (
        <p>Loading booking details...</p>
      ) : cancellationDetails ? (
        <div className="bg-white shadow-lg rounded-lg p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-2">Booking ID: {bookingId}</h2>
          <p className="text-gray-600 mb-4">Please review the cancellation policy:</p>
          <ul className="list-disc list-inside text-gray-600 mb-4">
            <li>More than 48 hours before start: 10% deduction from partial amount.</li>
            <li>More than 24 hours before start: 30% deduction from partial amount.</li>
            <li>Less than 2 hours before start: Full amount deducted.</li>
          </ul>
          <p className="text-gray-700 font-semibold mb-4">
            Remaining Partial Amount: ${cancellationDetails.partial_amount.toFixed(2)}
          </p>
          <button
            className="bg-red-600 text-white py-2 px-8 rounded-md shadow-md hover:bg-red-700 transition duration-300"
            onClick={handleConfirmCancellation}
          >
            Confirm Cancellation
          </button>
          <button
            className="ml-4 bg-gray-500 text-white py-2 px-8 rounded-md shadow-md hover:bg-gray-600 transition duration-300"
            onClick={() => navigate('/my-bookings')}
          >
            Go Back
          </button>
        </div>
      ) : (
        <p className="text-gray-500">No booking details found.</p>
      )}
    </div>
  );
}

export default CancellationPage;
