import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const userId = sessionStorage.getItem('userId'); // Fetching userId from session storage

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/booking/mybookings?userId=${userId}`);
        setBookings(response.data);
      } catch (error) {
        console.error('Error fetching bookings:', error);
      }
    };

    if (userId) {
      fetchBookings();
    }
  }, [userId]);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">My Bookings</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div
              key={booking.booking_id}
              className="bg-white border border-gray-200 rounded-lg p-5 shadow-md transition transform hover:scale-105 hover:shadow-lg"
            >
              <h2 className="text-lg font-semibold text-gray-700 mb-2">Booking ID: {booking.booking_id}</h2>
              <p className="text-gray-600"><strong>Tasker ID:</strong> {booking.tasker_id}</p>
              <p className="text-gray-600"><strong>Total Amount:</strong> ${booking.total_amount.toFixed(2)}</p>
              <p className="text-gray-600"><strong>Paid Amount:</strong> ${booking.partial_amount.toFixed(2)}</p>
              <p className="text-gray-600">
                <strong>Status:</strong> 
                <span className={`ml-2 font-semibold ${booking.task_status === 'COMPLETED' ? 'text-green-600' : 'text-yellow-500'}`}>
                  {booking.task_status}
                </span>
              </p>
              <p className="text-gray-600"><strong>Start Date:</strong> {new Date(booking.start_date).toLocaleDateString()}</p>
              <p className="text-gray-600"><strong>End Date:</strong> {new Date(booking.end_date).toLocaleDateString()}</p>
              <p className="text-gray-600"><strong>Total Hours:</strong> {booking.total_hours} hrs</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No bookings available.</p>
        )}
      </div>
    </div>
  );
}

export default MyBookings;
