import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/login/login', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log(response.data);
      navigate('/');
      // Assume you receive `token` from the backend on login
localStorage.setItem('authToken', token);

    } catch (error) {
      console.error('Error logging in:', error.response ? error.response.data : error.message);
    }
  };

  const backgroundImageStyle = {
    backgroundImage: `url("/login-image.jpg")`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const loginFormStyle = {
    backgroundColor: 'rgba(255, 0, 0, 0.8)',
    borderRadius: '20px',
    padding: '30px',
    maxWidth: '300px',
    width: '100%',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
  };

  const inputStyle = {
    width: 'calc(100% - 20px)',
    padding: '10px',
    border: '2px solid #ccc',
    borderRadius: '10px',
    marginBottom: '10px',
  };

  const buttonStyle = {
    width: '100%',
    backgroundColor: '#007bff',
    color: 'white',
    padding: '10px',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer',
    marginBottom: '10px',
  };

  const linkStyle = {
    color: '#0066cc',
    textDecoration: 'none',
    display: 'inline-block',
    marginTop: '20px',
  };

  return (
    <div style={backgroundImageStyle}>
      <div style={loginFormStyle}>
        <h2 style={{ marginBottom: '20px', color: '#333' }}>Tasker Login</h2>
        <form onSubmit={handleSubmit} method="post">
          <div style={{ marginBottom: '20px' }}>
            <label htmlFor="email" style={{ display: 'block' }}>Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              style={inputStyle}
              required
            />
          </div>

          <div style={{ marginBottom: '30px' }}>
            <label htmlFor="password" style={{ display: 'block' }}>Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              style={inputStyle}
              required
            />
          </div>

          <button type="submit" style={buttonStyle}>
            Login
          </button>
        </form>

        <div>
          <a href="/Home" style={linkStyle}>Home Page</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
