import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function TaskersList() {
  const [taskers, setTaskers] = useState([]);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [selectedTasker, setSelectedTasker] = useState(null); // To store selected tasker details for the panel
  const location = useLocation();
  const navigate = useNavigate();
  const serviceId = location.state?.serviceId;

  useEffect(() => {
    const fetchTaskers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/Tasker/Taskers', {
          params: { serviceId },
        });
        setTaskers(response.data);
      } catch (error) {
        console.error('Error fetching taskers:', error);
      }
    };

    if (serviceId) {
      fetchTaskers();
    }
  }, [serviceId]);

  // Handle booking action
  const handleBookTasker = (tasker) => {
    // Save tasker details in session storage
    sessionStorage.setItem('taskerId', tasker.tasker_Profile_Id);
    sessionStorage.setItem('hourlyRate', tasker.hourlyRate);

    // Navigate to the Payment page
    navigate('/payment', { state: { taskerId: tasker.tasker_Profile_Id } });
  };

  // Open the sliding panel with tasker details
  const handleOpenPanel = (tasker) => {
    setSelectedTasker(tasker);
    setIsPanelOpen(true);
  };

  // Close the sliding panel
  const handleClosePanel = () => {
    setIsPanelOpen(false);
    setSelectedTasker(null);
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-100 via-indigo-100 to-pink-100">
      <div className="w-full max-w-4xl bg-white p-8 rounded-lg shadow-xl">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">Available Taskers</h1>

        {/* Tasker Grid */}
        <div className="taskers-container grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {taskers.length > 0 ? (
            taskers.map((tasker) => (
              <div
                key={tasker.tasker_Profile_Id}
                className="tasker-card bg-white p-4 rounded-lg shadow-lg transition transform hover:scale-105 hover:shadow-2xl"
              >
                <img
                  src={`http://localhost:5000/${tasker.taskerImage}`} // Tasker Image
                  alt={`${tasker.taskerName}'s profile`}
                  className="tasker-image w-full h-48 object-cover rounded-md mb-4"
                />
                <div>
                  <h2 className="text-xl font-semibold text-gray-800">{tasker.taskerName}</h2>
                  <p className="text-gray-600 mt-2">Experience: {tasker.experience} years</p>
                  <p className="text-gray-600">Hourly Rate: ${tasker.hourlyRate}</p>
                  <button
                    onClick={() => handleBookTasker(tasker)}
                    className="book-button mt-4 w-full bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-300"
                  >
                    Book Now
                  </button>
                  <button
                    onClick={() => handleOpenPanel(tasker)}
                    className="more-info-button mt-2 w-full bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-300"
                  >
                    More Info
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="text-gray-500 text-center col-span-full">No taskers available.</p>
          )}
        </div>
      </div>

      {/* Sliding Panel */}
      <div
        className={`fixed inset-0 bg-gray-500 bg-opacity-75 z-50 transform ${
          isPanelOpen ? 'translate-x-0' : 'translate-x-full'
        } transition-transform duration-300 ease-in-out`}
        onClick={handleClosePanel}
      ></div>

      <div
        className={`fixed right-0 top-0 w-1/3 bg-white h-full shadow-lg transition-transform duration-300 ease-in-out z-50 ${
          isPanelOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        {selectedTasker && (
          <div className="p-6">
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">{selectedTasker.taskerName}</h2>
            <img
              src={`http://localhost:5000/${selectedTasker.taskerImage}`}
              alt={`${selectedTasker.taskerName}'s profile`}
              className="w-full h-48 object-cover rounded-md mb-4"
            />
            <p className="text-gray-600 mb-2">Experience: {selectedTasker.experience} years</p>
            <p className="text-gray-600 mb-2">Hourly Rate: ${selectedTasker.hourlyRate}</p>
            <p className="text-gray-600 mb-4">{selectedTasker.description}</p>

            <div className="flex justify-between">
              <button
                onClick={() => handleBookTasker(selectedTasker)}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-300"
              >
                Book Now
              </button>
              <button
                onClick={handleClosePanel}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default TaskersList;
