import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};
  const hourlyRate = sessionStorage.getItem('hourlyRate');  // Fetch hourly rate from session storage

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [hours, setHours] = useState(0);
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.async = true;
      document.body.appendChild(script);
    };
    loadRazorpayScript();
  }, []);

  const calculateHoursAndAmount = () => {
    const start = new Date(`1970-01-01T${startTime}`);
    const end = new Date(`1970-01-01T${endTime}`);

    if (end <= start || !hourlyRate) {
      setHours(0);
      setAmount(0);
      return;
    }

    // Calculate difference in milliseconds
    const diffMs = end - start;
    let diffHours = diffMs / (1000 * 60 * 60);  // Convert to hours

    // Round up if there are any extra minutes
    if (diffHours % 1 !== 0) {
      diffHours = Math.ceil(diffHours);
    }

    // Apply max limit of 10 hours
    const calculatedHours = Math.min(diffHours, 10);
    setHours(calculatedHours);
    setAmount(calculatedHours * hourlyRate);
  };

  useEffect(() => {
    if (startTime && endTime) {
      calculateHoursAndAmount();
    }
  }, [startTime, endTime]);

  const handlePayment = async (e) => {
    e.preventDefault();

    if (!window.Razorpay) {
      alert('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    const totalAmount = amount * 100;  // Convert to paise
    const partialAmount = totalAmount / 2;

    const options = {
      key: 'rzp_test_KA0MVYPAHSnecF',
      amount: partialAmount,  // Set to partial amount
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async function (response) {
        try {
          await axios.post('/api/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            taskerId,
            userId: sessionStorage.getItem('userId'), // Fetch userId from session storage
            totalAmount,
            partialAmount,
            startTime,
            endTime,
            hours,
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container">
      <div className="billing-info">
        <h2>Billing Information</h2>
        <form onSubmit={handlePayment}>
          <label>
            Name:
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
          </label>
          <label>
            Email:
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </label>
          <label>
            Contact:
            <input type="text" value={contact} onChange={(e) => setContact(e.target.value)} required />
          </label>
          <label>
            Start Time:
            <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} required />
          </label>
          <label>
            End Time:
            <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} required />
          </label>
          <p>Total Hours: {hours}</p>
          <p>Total Amount: ₹{amount / 100}</p>
          <button type="submit">Pay Now</button>
        </form>
      </div>
    </div>
  );
};

export default Payment;
