import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [hours, setHours] = useState(1);
  const [amount, setAmount] = useState(50000);  // Amount in paise (₹50000)

  // Load Razorpay script
  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.async = true;
      script.onload = () => console.log('Razorpay script loaded');
      script.onerror = () => console.log('Failed to load Razorpay script');
      document.body.appendChild(script);
    };

    loadRazorpayScript();
  }, []);

  const handlePayment = async (e) => {
    e.preventDefault();

    // Ensure Razorpay script is loaded
    if (typeof window.Razorpay === 'undefined') {
      alert('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    const options = {
      key: 'rzp_test_KA0MVYPAHSnecF',  // Test key
      amount: amount,  // Amount in paise (₹50000 => ₹500.00)
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async function (response) {
        try {
          await axios.post('/api/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            taskerId,
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container min-h-screen bg-gradient-to-r from-pink-400 to-yellow-300 flex justify-center items-center">
      <div className="payment-form flex w-full max-w-6xl bg-white rounded-lg shadow-lg overflow-hidden">
        {/* Left side - Billing Information */}
        <div className="billing-info p-6 w-1/2 border-r border-gray-300">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Billing Information</h2>
          <form onSubmit={handlePayment}>
            <div className="mb-4">
              <label htmlFor="name" className="block text-gray-600">Name</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="email" className="block text-gray-600">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="contact" className="block text-gray-600">Contact Number</label>
              <input
                type="tel"
                id="contact"
                value={contact}
                onChange={(e) => setContact(e.target.value)}
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="hours" className="block text-gray-600">Hours of Work</label>
              <input
                type="number"
                id="hours"
                value={hours}
                onChange={(e) => setHours(e.target.value)}
                min="1"
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </form>
        </div>

        {/* Right side - Checkout Summary */}
        <div className="checkout-summary p-6 w-1/2">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Checkout</h2>
          <div className="summary-details mb-6">
            <p className="text-gray-700 mb-2"><strong>Tasker:</strong> {taskerId ? `Tasker #${taskerId}` : 'Unknown'}</p>
            <p className="text-gray-700 mb-2"><strong>Hours:</strong> {hours} hr(s)</p>
            <p className="text-gray-700 mb-2"><strong>Amount:</strong> ₹{(amount / 100).toFixed(2)}{<light>   This is partial payment and later the actual amount is collected)</light>}</p>
          </div>
          <button
            onClick={handlePayment}
            className="payment-button w-full py-3 text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Pay Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default Payment;
