import React, { useState, useEffect } from "react";
import axios from "axios";

const TaskerPayments = () => {
  const [payments, setPayments] = useState([]);

  // Fetch the payment status when the component mounts
  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const response = await axios.get('/api/tasker-payments'); // Adjust the endpoint
        setPayments(response.data.payments);
      } catch (error) {
        console.error('Error fetching payment status:', error);
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="container mx-auto my-8 p-4">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">
        Payment Status for Your Services
      </h2>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow-lg">
          <thead>
            <tr className="text-left text-gray-600 bg-gray-100">
              <th className="px-6 py-3">Customer Name</th>
              <th className="px-6 py-3">Total Amount</th>
              <th className="px-6 py-3">Partial Amount</th>
              <th className="px-6 py-3">Balance Amount</th>
              <th className="px-6 py-3">Payment Status</th>
            </tr>
          </thead>
          <tbody>
            {payments.length > 0 ? (
              payments.map((payment) => (
                <tr key={payment.bookingId} className="border-b border-gray-200">
                  <td className="px-6 py-4">{payment.customerFirstName} {payment.customerLastName}</td>
                  <td className="px-6 py-4">${payment.total_amount.toFixed(2)}</td>
                  <td className="px-6 py-4">${payment.partial_amount.toFixed(2)}</td>
                  <td className="px-6 py-4">${payment.balance_amount.toFixed(2)}</td>
                  <td className="px-6 py-4">
                    <span
                      className={`px-4 py-2 text-white font-semibold rounded-lg ${
                        payment.payment_status === "Pending"
                          ? "bg-red-500"
                          : payment.payment_status === "Partially Paid"
                          ? "bg-yellow-500"
                          : "bg-green-500"
                      }`}
                    >
                      {payment.payment_status}
                    </span>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center py-4 text-gray-500">No payments yet.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TaskerPayments;
