import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Services from './pages/Services';
import SignUp from './pages/SignUp';
import Login from './pages/SignIn';
import BecomeTasker from './pages/BecomeTasker';
import CleaningWork from './pages/CleaningWork';
import ConstructionWork from './pages/ConstructionWork';
import DeliveryWork from './pages/DeliveryWork';
import FieldWork from './pages/FieldWork';
//import SignUpLogin from './pages/SignUpLogin';
import TaskerDashboard from './pages/TaskerDashboard';
import CreateProfile from './pages/CreateProfile'; // Import CreateProfile component
import Footer from './components/Footer';

function App() {
  // Simulating authentication status (for other routes if needed)
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Mocked logic to check authentication status (replace with real logic)
  useEffect(() => {
    const authStatus = localStorage.getItem('isAuthenticated');
    setIsAuthenticated(!!authStatus); // Convert to boolean
  }, []);

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <Navbar />
        <div className="flex-grow">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/services" element={<Services />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/login" element={<Login />} />

            {/* Route for Become a Tasker, no authentication check */}
            <Route
              path="/become-tasker"
              element={<TaskerDashboard />} // Directly navigate to TaskerDashboard
            />

            <Route path="/cleaning-work" element={<CleaningWork />} />
            <Route path="/construction-work" element={<ConstructionWork />} />
            <Route path="/delivery-work" element={<DeliveryWork />} />
            <Route path="/field-work" element={<FieldWork />} />

            {/* Tasker Dashboard Route */}
            <Route path="/dashboard" element={<TaskerDashboard />} />

            {/* Create Profile Route */}
            <Route path="/create-profile" element={<CreateProfile />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
