import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function TaskersList() {
  const [taskers, setTaskers] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();
  const serviceId = location.state?.serviceId;

  useEffect(() => {
    const fetchTaskers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/Tasker/Taskers', {
          params: { serviceId },
        });
        setTaskers(response.data);
      } catch (error) {
        console.error('Error fetching taskers:', error);
      }
    };

    if (serviceId) {
      fetchTaskers();
    }
  }, [serviceId]);

  // Handle booking action
  const handleBookTasker = (tasker) => {
    // Save tasker details in session storage
    sessionStorage.setItem('taskerId', tasker.tasker_Profile_Id);
    sessionStorage.setItem('hourlyRate', tasker.hourlyRate);

    // Navigate to the Payment page
    navigate('/payment', { state: { taskerId: tasker.tasker_Profile_Id } });
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Available Taskers</h1>

      {/* Taskers container with horizontal scroll */}
      <div className="taskers-container overflow-x-auto py-4" style={styles.taskersContainer}>
        {taskers.length > 0 ? (
          taskers.map((tasker) => (
            <div
              key={tasker.tasker_Profile_Id}
              className="tasker-card bg-white p-4 rounded-lg shadow-lg max-w-xs flex-shrink-0"
              style={styles.taskerCard}
            >
              {/* Tasker Image */}
              <img
                src={`http://localhost:5000/${tasker.taskerImage}`} // Display tasker image
                alt={`${tasker.taskerName}'s profile`}
                className="tasker-image w-full h-48 object-cover rounded-md mb-4"
                style={styles.taskerImage}
              />
              <div>
                <h2 className="tasker-name text-xl font-semibold text-gray-800" style={styles.taskerName}>
                  {tasker.taskerName}
                </h2>
                <p className="tasker-experience text-gray-600 mt-2" style={styles.taskerExperience}>
                  Experience: {tasker.experience} years
                </p>
                <p className="tasker-rate text-gray-600" style={styles.taskerRate}>
                  Hourly Rate: ${tasker.hourlyRate}
                </p>
                <button
                  onClick={() => handleBookTasker(tasker)}
                  className="book-button mt-4 w-full bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-300"
                  style={styles.bookButton}
                >
                  Book Now
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-center col-span-full">No taskers available.</p>
        )}
      </div>
    </div>
  );
}

// Inline styles for custom CSS
const styles = {
  taskersContainer: {
    background: 'linear-gradient(135deg, #f7f7f7 0%, #f0f0f0 100%)', // Subtle gradient background
    paddingTop: '20px',
    paddingBottom: '20px',
  },
  taskerCard: {
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    backgroundColor: '#ffffff',
  },
  taskerCardHover: {
    transform: 'translateY(-5px)',
    boxShadow: '0 10px 20px rgba(0, 0, 0, 0.1)',
  },
  taskerImage: {
    borderRadius: '8px', // Smooth rounded edges for the image
  },
  taskerName: {
    fontWeight: '600', // Slightly bolder name for prominence
    color: '#4a4a4a', // Darker text for the name
  },
  taskerExperience: {
    marginTop: '10px',
    color: '#6c6c6c', // Lighter color for experience
  },
  taskerRate: {
    marginTop: '5px',
    color: '#007bff', // Blue color for hourly rate
    fontWeight: '500',
  },
  bookButton: {
    backgroundColor: '#007bff',
    transition: 'background-color 0.3s ease',
  },
  bookButtonHover: {
    backgroundColor: '#0056b3',
  },
};

export default TaskersList;
