import React from 'react';
import { Link } from 'react-router-dom';
import background from './images/background.jpg'
import SignUp from './SignUp';
import Login from './Login';

function AuthLanding() {
  return (
    <div
      className="h-screen flex items-center justify-center bg-cover bg-center"
      style={{ backgroundImage: `url(${background})` }}    >
      <div className="bg-white bg-opacity-70 p-10 rounded-lg shadow-lg text-center">
        <h2 className="text-4xl font-bold mb-6 text-gray-800">Welcome to Udyoga</h2>
        <p className="text-gray-700 mb-8">Please choose an option to continue</p>

        <div className="flex justify-center space-x-4">
          <Link to="/signin">
            <button className="bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg hover:bg-blue-700 transition duration-300">
              Sign In
            </button>
          </Link>
          <Link to="/signup">
            <button className="bg-green-600 text-white font-semibold px-6 py-3 rounded-lg hover:bg-green-700 transition duration-300">
              Sign Up
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default AuthLanding;
