import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [hourlyRate, setHourlyRate] = useState(0);
  const [duration, setDuration] = useState(1); // in hours, adjust as needed
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    // Retrieve hourly rate from session storage
    const storedHourlyRate = sessionStorage.getItem('hourlyRate');
    if (storedHourlyRate) {
      setHourlyRate(parseFloat(storedHourlyRate));
      setAmount(parseFloat(storedHourlyRate) * duration);
    }
  }, [duration]);

  const handlePayment = async (e) => {
    e.preventDefault();

    const options = {
      key: 'YOUR_RAZORPAY_KEY',
      amount: amount * 100, // convert to paisa
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async function (response) {
        try {
          await axios.post('/api/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            taskerId,
            amount
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container p-6 max-w-md mx-auto border rounded shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Payment for Tasker Booking</h2>
      <form onSubmit={handlePayment}>
        <div className="mb-4">
          <label className="block text-gray-700">Name</label>
          <input type="text" className="border rounded w-full py-2 px-3" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Email</label>
          <input type="email" className="border rounded w-full py-2 px-3" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Contact Number</label>
          <input type="tel" className="border rounded w-full py-2 px-3" value={contact} onChange={(e) => setContact(e.target.value)} required />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Duration (in hours)</label>
          <input type="number" className="border rounded w-full py-2 px-3" value={duration} onChange={(e) => setDuration(e.target.value)} min="1" required />
        </div>
        <p className="mb-4 text-gray-700">Hourly Rate: ₹{hourlyRate}</p>
        <p className="mb-4 font-bold text-gray-700">Total Amount: ₹{amount}</p>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full">
          Pay Now
        </button>
      </form>
    </div>
  );
};

export default Payment;
