import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState(''); // State to hold error messages
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrorMessage(''); // Clear error message on input change
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/login/login', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      // Extract the token and userID from the response
      const { token, userID, name } = response.data;

      // Store the token and userID in localStorage
      localStorage.setItem('authToken', token);
      localStorage.setItem('userID', userID); // Store userID correctly
      localStorage.setItem('name', name);

      // Redirect to the home page or any other page after successful login
      navigate('/'); // Change this to the desired path after login

    } catch (error) {
      // Improved error handling
      const message = error.response?.data?.message || 'Error logging in. Please try again.';
      setErrorMessage(message);
      console.error('Error logging in:', message);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800">Login</h1>
      {errorMessage && <p className="text-red-500">{errorMessage}</p>} {/* Display error message */}
      <form onSubmit={handleSubmit} className="mt-4">
        <div>
          <label className="block text-gray-700">Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="border rounded p-2 w-full"
          />
        </div>
        <div className="mt-4">
          <label className="block text-gray-700">Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            className="border rounded p-2 w-full"
          />
        </div>
        <button type="submit" className="mt-4 bg-blue-500 text-white p-2 rounded">
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
