import React from 'react';

function App() {
  const jobCategories = [
    { name: "Cleaning Work", image: "cleaning.jpg" },
    { name: "Construction", image: "construction.jpg" },
    { name: "Gardening Work", image: "gardening.jpg" },
    { name: "Field Work", image: "fieldwork.jpg" },
  ];

  return (
    <div>
      {/* Navigation Bar */}
      <nav className="bg-blue-600 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-white text-2xl font-bold">Udyoga</h1>
          <ul className="flex space-x-6">
            <li><a href="#" className="text-white hover:text-gray-300">Services</a></li>
            <li><a href="#" className="text-white hover:text-gray-300">Sign Up</a></li>
            <li><a href="#" className="text-white hover:text-gray-300">Login</a></li>
            <li><a href="#" className="text-white hover:text-gray-300">Become a Tasker</a></li>
          </ul>
        </div>
      </nav>

      {/* Main Content */}
      <div className="min-h-screen bg-gray-100 p-6">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">Rural Wage Work Employment</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {jobCategories.map((category) => (
            <div key={category.name} className="bg-white shadow-md rounded-lg overflow-hidden hover:shadow-lg transition-shadow duration-300">
              <img src={category.image} alt={category.name} className="w-full h-40 object-cover" />
              <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-700">{category.name}</h2>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
