import React, { useState } from 'react';

const AuthLoading = ({ isLoading }) => {
  const [isSignUp, setIsSignUp] = useState(false);

  const handleSignIn = () => {
    setIsSignUp(false);
    // Logic for signing in (could redirect to sign-in page or trigger sign-in modal)
        <Link 
          to="/signup" 
          className="absolute top-6 right-6 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition">
          Become a Tasker
        </Link>
    console.log("Sign In clicked");
  };

  const handleSignUp = () => {
    setIsSignUp(true);
    // Logic for signing up (could redirect to sign-up page or trigger sign-up modal)
    console.log("Sign Up clicked");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4">Welcome to Our Service</h1>
      <p className="mb-8">Please {isSignUp ? 'sign up' : 'sign in'} to continue.</p>

      {isLoading ? (
        <div className="loader">Loading...</div> // You can replace this with a spinner or loading animation
      ) : (
        <div className="flex space-x-4">
          <button
            onClick={handleSignIn}
            className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-200"
          >
            Sign In
          </button>
          <button
            onClick={handleSignUp}
            className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition duration-200"
          >
            Sign Up
          </button>
        </div>
      )}
    </div>
  );
};

export default AuthLoading;
