import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function HomeServices() {
  const [selectedSubService, setSelectedSubService] = useState(null);
  const [experience, setExperience] = useState('');
  const [hourlyRate, setHourlyRate] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Main service type passed via route state (e.g., "Home Services")
  const serviceType = location.state?.serviceType || 'Home Services';

  const subServices = [
    { id: 1, name: 'Plumbing', description: 'Plumbing services for home repairs.' },
    { id: 2, name: 'Carpentry', description: 'Carpentry services including furniture repairs.' },
    { id: 3, name: 'Electrical Work', description: 'Electrical services like wiring and appliance repair.' },

  ];

  const handleSubServiceSelect = (subServiceId) => {
    setSelectedSubService(subServiceId);
    setExperience('');
    setHourlyRate('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedService = subServices.find(s => s.id === selectedSubService)?.name;

    // Prepare data for submission
    const serviceData = {
      mainService: serviceType,
      subService: selectedService,
      experience,
      hourlyRate
    };

    console.log(serviceData); // Replace this with a request to send data to the backend

    // Reset after submission
    setSelectedSubService(null);
    setExperience('');
    setHourlyRate('');
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800">{serviceType}</h1>
      <p className="mt-4 text-gray-600">
        Choose from the following home services:
      </p>

      <div className="flex flex-col gap-6 mt-8">
        {subServices.map((subService) => (
          <div
            key={subService.id}
            onClick={() => handleSubServiceSelect(subService.id)}
            className={`cursor-pointer border border-gray-200 rounded-lg p-4 shadow-md transition-transform transform hover:scale-105 ${
              selectedSubService === subService.id ? 'border-blue-500' : 'border-transparent'
            }`}
          >
            <h3 className="text-xl font-bold text-gray-800">{subService.name}</h3>
            <p className="mt-2 text-gray-600">{subService.description}</p>

            {selectedSubService === subService.id && (
              <form onClick={(e) => e.stopPropagation()} onSubmit={handleSubmit} className="mt-4 bg-gray-50 p-4 rounded-lg shadow-inner">
                <label className="block text-gray-700 font-bold mb-2">Years of Experience</label>
                <input
                  type="number"
                  value={experience}
                  onChange={(e) => setExperience(e.target.value)}
                  placeholder="Enter your years of experience"
                  className="w-full p-2 border border-gray-300 rounded-lg mb-4"
                  required
                />

                <label className="block text-gray-700 font-bold mb-2">Hourly Rate ($)</label>
                <input
                  type="number"
                  value={hourlyRate}
                  onChange={(e) => setHourlyRate(e.target.value)}
                  placeholder="Enter your hourly rate"
                  className="w-full p-2 border border-gray-300 rounded-lg mb-4"
                  required
                />

                <button
                  type="submit"
                  className="bg-blue-600 text-white px-4 py-2 rounded-lg"
                >
                  Submit
                </button>
              </form>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomeServices;
