// AuthLoading.js
import React from 'react';
import { useHistory } from 'react-router-dom';

const AuthLoading = ({ isLoading }) => {
  const history = useHistory();

  const handleSignUp = () => {
    history.push('/signup'); // Redirect to the sign-up page
  };

  const handleSignIn = () => {
    history.push('/login'); // Redirect to the login page
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      {isLoading ? (
        <div className="loader">Loading...</div> // Replace with your loading indicator
      ) : (
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Authentication Required</h2>
          <p className="mb-6">You need to sign in or sign up to continue.</p>
          <div className="flex space-x-4">
            <button
              onClick={handleSignIn}
              className="bg-blue-500 text-white py-2 px-4 rounded"
            >
              Sign In
            </button>
            <button
              onClick={handleSignUp}
              className="bg-green-500 text-white py-2 px-4 rounded"
            >
              Sign Up
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AuthLoading;
