import React, { useState } from 'react';

function CreateProfile() {
  const [formData, setFormData] = useState({
    taskCategory: '',
    subCategories: [],
    insuranceOption: 'none',
    specialInstructions: '',
    date: new Date().toISOString().split('T')[0],
    address: {
      houseNumber: '',
      area: '',
      city: '',
      state: '',
      pincode: '',
    },
    availabilityStatus: 'available', // default value
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Handle nested object for address
    if (name.startsWith('address.')) {
      const addressField = name.split('.')[1];
      setFormData({
        ...formData,
        address: {
          ...formData.address,
          [addressField]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleMultiSelectChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setFormData({
      ...formData,
      subCategories: selectedOptions,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/taskers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      if (response.ok) {
        console.log(result);
        // Optionally, reset the form or display a success message
      } else {
        console.error(result.error);
        // Optionally, display an error message
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const subCategories = {
    "Home Services": ['Plumbing', 'Carpentry', 'Electrical Work', 'Housekeeping', 'Laundry Services'],
    "Personal Services": ['Personal Training', 'Pet Care', 'Tutoring'],
    "Event Services": ['Event Planning', 'Photography', 'Catering'],
    "Health & Wellness": ['Fitness Training', 'Massage Therapy', 'Wellness Services'],
    "Automotive Services": ['Car Washing', 'Vehicle Maintenance'],
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10">
      <div className="container mx-auto max-w-3xl bg-white rounded-lg shadow-lg p-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Become a Tasker</h2>

        <form onSubmit={handleSubmit}>
          {/* Task Category */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">Task Category</h3>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Select Category:</label>
              <select
                name="taskCategory"
                value={formData.taskCategory}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              >
                <option value="">Select a Category</option>
                {Object.keys(subCategories).map((category) => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>

            {formData.taskCategory && (
              <div className="mb-4">
                <label className="block text-gray-700 mb-2">Select Subcategories (Hold Ctrl/Cmd to select multiple):</label>
                <select
                  name="subCategories"
                  multiple
                  value={formData.subCategories}
                  onChange={handleMultiSelectChange}
                  className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                  required
                >
                  {subCategories[formData.taskCategory].map((subCat) => (
                    <option key={subCat} value={subCat}>{subCat}</option>
                  ))}
                </select>
              </div>
            )}
          </div>

          {/* Address Fields */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">Address Information</h3>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">House Number:</label>
              <input
                type="text"
                name="address.houseNumber"
                value={formData.address.houseNumber}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Area:</label>
              <input
                type="text"
                name="address.area"
                value={formData.address.area}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">City:</label>
              <input
                type="text"
                name="address.city"
                value={formData.address.city}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">State:</label>
              <input
                type="text"
                name="address.state"
                value={formData.address.state}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Pincode:</label>
              <input
                type="text"
                name="address.pincode"
                value={formData.address.pincode}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
            </div>
          </div>

          {/* Insurance Option */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">Insurance Option</h3>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Select Insurance Option:</label>
              <select
                name="insuranceOption"
                value={formData.insuranceOption}
                onChange={handleChange}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
              >
                <option value="none">None</option>
                <option value="basic">Basic Coverage</option>
                <option value="premium">Premium Coverage</option>
              </select>
            </div>
          </div>

          {/* Additional Information */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">Additional Information</h3>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Any special instructions or details:</label>
              <textarea
                name="specialInstructions"
                value={formData.specialInstructions}
                onChange={handleChange}
                rows="3"
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>

          {/* Availability Status */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-700 mb-4">Availability Status</h3>
            <select
              name="availabilityStatus"
              value={formData.availabilityStatus}
              onChange={handleChange}
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:border-blue-500"
            >
              <option value="available">Available</option>
              <option value="not_available">Not Available</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-teal-500 text-white py-2 px-4 rounded-md hover:bg-teal-600 transition duration-200"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default CreateProfile;
