import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  //const [amount, setAmount] = useState(50000); // Amount in paise (₹500.00)

  const handlePayment = async (e) => {
    e.preventDefault();

    // Generate order details on the backend
    try {
      const { data } = await axios.post('/api/payment/create-order', {
        amount,
        taskerId,
      });

      const options = {
        key: 'YOUR_RAZORPAY_KEY', // Replace with your Razorpay key ID
        amount: data.amount,
        currency: 'INR',
        name: 'Your Company Name',
        description: 'Booking Tasker',
        order_id: data.orderId, // Razorpay order ID created in backend
        handler: async function (response) {
          // Send payment details to backend for verification
          try {
            await axios.post('/api/payment/verify', {
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_order_id: response.razorpay_order_id,
              razorpay_signature: response.razorpay_signature,
              taskerId,
            });
            alert('Payment successful!');
          } catch (error) {
            alert('Payment verification failed.');
          }
        },
        prefill: { name, email, contact },
        notes: { taskerId },
        theme: { color: '#F37254' },
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error('Error in creating order:', error);
      alert('Error in initiating payment. Please try again.');
    }
  };

  return (
    <div className="payment-container p-6 max-w-md mx-auto border rounded shadow-lg">
      <h2 className="text-2xl font-bold mb-4">Payment for Tasker Booking</h2>
      <form onSubmit={handlePayment}>
        <div className="mb-4">
          <label className="block text-gray-700">Name</label>
          <input
            type="text"
            className="border rounded w-full py-2 px-3"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Email</label>
          <input
            type="email"
            className="border rounded w-full py-2 px-3"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Contact Number</label>
          <input
            type="tel"
            className="border rounded w-full py-2 px-3"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full">
          Pay Now
        </button>
      </form>
    </div>
  );
};

export default Payment;
