import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const [review, setReview] = useState('');
  const [rating, setRating] = useState(10);
  const userId = sessionStorage.getItem('userID');

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/myBookings?userId=${userId}`);
        setBookings(response.data);
      } catch (error) {
        console.error('Error fetching bookings:', error);
      }
    };

    if (userId) {
      fetchBookings();
    }
  }, [userId]);

  const cancelBooking = async (bookingId, startDate) => {
    const hoursUntilStart = (new Date(startDate) - new Date()) / (1000 * 60 * 60);
    let deduction = 0;

    if (hoursUntilStart < 2) {
      deduction = 1.0; // 100%
    } else if (hoursUntilStart < 24) {
      deduction = 0.5; // 50%
    } else if (hoursUntilStart < 48) {
      deduction = 0.3; // 30%
    } else {
      deduction = 0.1; // 10%
    }

    try {
      await axios.post(`http://localhost:5000/api/cancelBooking`, {
        bookingId,
        deduction,
      });
      alert('Booking cancelled with a deduction of ' + deduction * 100 + '%');
      // Fetch updated bookings
      fetchBookings();
    } catch (error) {
      console.error('Error cancelling booking:', error);
    }
  };

  const finishBooking = async (bookingId) => {
    try {
      await axios.post(`http://localhost:5000/api/finishBooking`, {
        bookingId,
        review,
        rating,
      });
      alert('Booking completed. Thank you for your review!');
      // Fetch updated bookings
      fetchBookings();
    } catch (error) {
      console.error('Error finishing booking:', error);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-semibold text-gray-800 mb-8">My Bookings</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {bookings.length > 0 ? (
          bookings.map(booking => (
            <div
              key={booking.booking_id}
              className="bg-gradient-to-r from-indigo-500 via-purple-600 to-blue-700 rounded-lg p-6 shadow-xl transform transition duration-300 hover:scale-105 hover:shadow-2xl"
            >
              <h2 className="text-xl font-semibold text-white mb-3">Booking ID: {booking.booking_id}</h2>
              <p className="text-white mb-2"><strong>Tasker ID:</strong> {booking.tasker_id}</p>
              <p className="text-white mb-2"><strong>Total Amount:</strong> ${booking.total_amount.toFixed(2)}</p>
              <p className="text-white mb-2"><strong>Paid Amount:</strong> ${booking.partial_amount.toFixed(2)}</p>
              <p className="text-white mb-2">
                <strong>Status:</strong> 
                <span className={`ml-2 font-semibold ${booking.task_status === 'COMPLETED' ? 'text-green-300' : 'text-yellow-300'}`}>
                  {booking.task_status}
                </span>
              </p>
              <p className="text-white mb-2"><strong>Start Date:</strong> {new Date(booking.start_date).toLocaleDateString()}</p>
              <p className="text-white mb-4"><strong>End Date:</strong> {new Date(booking.end_date).toLocaleDateString()}</p>
              <p className="text-white mb-4"><strong>Total Hours:</strong> {booking.total_hours} hrs</p>

              {/* Action Buttons */}
              <div className="mt-4 flex space-x-4">
                <button 
                  onClick={() => cancelBooking(booking.booking_id, booking.start_date)}
                  className="bg-red-600 text-white py-2 px-6 rounded-md shadow-md transition duration-300 transform hover:bg-red-700 hover:scale-105"
                >
                  Cancel Booking
                </button>
                <button 
                  onClick={() => finishBooking(booking.booking_id)}
                  className="bg-teal-500 text-white py-2 px-6 rounded-md shadow-md transition duration-300 transform hover:bg-teal-600 hover:scale-105"
                >
                  Finish
                </button>
              </div>

              {/* Review Form */}
              {booking.task_status === 'COMPLETED' && (
                <div className="mt-4">
                  <textarea 
                    className="w-full bg-gray-100 rounded-md p-2"
                    placeholder="Leave a review..."
                    value={review}
                    onChange={(e) => setReview(e.target.value)}
                  />
                  <input 
                    type="number" 
                    min="1" 
                    max="10" 
                    value={rating} 
                    onChange={(e) => setRating(e.target.value)} 
                    className="w-full mt-2 p-2 rounded-md bg-gray-100"
                    placeholder="Rating (1-10)"
                  />
                  <button 
                    onClick={() => finishBooking(booking.booking_id)}
                    className="bg-green-500 text-white py-2 px-6 mt-4 rounded-md shadow-md transition duration-300 transform hover:bg-green-600 hover:scale-105"
                  >
                    Submit Review
                  </button>
                </div>
              )}
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-lg">No bookings available.</p>
        )}
      </div>
    </div>
  );
}

export default MyBookings;
