import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  // Retrieve user data from session storage
  const hourlyRate = parseFloat(sessionStorage.getItem('hourlyRate')) || 0;
  const userId = sessionStorage.getItem('userId');
  const serviceId = sessionStorage.getItem('serviceId');

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [hours, setHours] = useState(1);
  const [amount, setAmount] = useState(hourlyRate * hours * 100); // In paise

  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.async = true;
      document.body.appendChild(script);
    };
    loadRazorpayScript();
  }, []);

  const handlePayment = async (e) => {
    e.preventDefault();
    if (!window.Razorpay) {
      alert('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    const totalAmount = hourlyRate * hours * 100;  // Total amount in paise
    const partialAmount = totalAmount / 2;

    const options = {
      key: 'rzp_test_KA0MVYPAHSnecF', // Replace with live key in production
      amount: partialAmount,
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async (response) => {
        try {
          await axios.post('/api/booking/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            userId,
            taskerId,
            serviceId,
            totalAmount,
            partialAmount,
            hours,
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container bg-gray-50 min-h-screen flex justify-center items-center py-10">
      <div className="billing-info bg-white p-8 rounded-lg shadow-lg w-full max-w-lg">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">Billing Information</h2>
        <form onSubmit={handlePayment}>
          <div className="space-y-4">
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Name"
              required
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="tel"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              placeholder="Contact"
              required
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="number"
              value={hours}
              onChange={(e) => setHours(e.target.value)}
              min="1"
              required
              placeholder="Hours"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
          </div>
          <div className="my-4">
            <p className="text-lg font-medium text-gray-800">Total Amount: ₹{(hourlyRate * hours).toFixed(2)}</p>
          </div>
          <button
            type="submit"
            className="w-full py-3 px-6 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          >
            Pay Now
          </button>
        </form>
      </div>
    </div>
  );
};

export default Payment;
