import React, { useState, useEffect } from 'react';

function TaskerProfile() {
  const [image, setImage] = useState(null);
  const [area, setArea] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [pincode, setPincode] = useState('');
  const [bio, setBio] = useState('');
  const userID = sessionStorage.getItem('userID');
  const userName = sessionStorage.getItem('name');

  useEffect(() => {
    async function fetchProfile() {
      try {
        const response = await fetch(`http://localhost:5000/api/TaskerProfile/${userID}`, {
          headers: {
            'Authorization': `Bearer ${sessionStorage.getItem('authToken')}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setArea(data.area);
          setCity(data.city);
          setState(data.state);
          setPincode(data.pincode);
          setBio(data.bio);
          if (data.image) setImage(data.image);
        } else {
          console.log('Profile not found or user is not authorized');
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    }

    if (userID) fetchProfile();
  }, [userID]);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('image', image);
    formData.append('area', area);
    formData.append('city', city);
    formData.append('state', state);
    formData.append('pincode', pincode);
    formData.append('bio', bio);
    formData.append('userID', userID);

    try {
      const response = await fetch('http://localhost:5000/api/TaskerProfile/TaskerProfile', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${sessionStorage.getItem('authToken')}`,
        },
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        const taskerProfileId = result.taskerProfileId;
        sessionStorage.setItem('taskerProfileId', taskerProfileId);
        console.log('Profile updated successfully, Tasker Profile ID:', taskerProfileId);
      } else {
        console.error('Error updating profile');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="bg-cover bg-center min-h-screen p-8" style={{ backgroundImage: 'url(/path/to/your/background-image.jpg)' }}>
      <div className="max-w-4xl mx-auto bg-black bg-opacity-75 rounded-lg shadow-lg p-8 flex">
        <div className="flex-shrink-0 mr-6">
          {image ? (
            <img
              src={typeof image === 'string' ? image : URL.createObjectURL(image)}
              alt="Profile"
              className="rounded-full border-4 border-white w-32 h-32 object-cover shadow-lg"
            />
          ) : (
            <div className="rounded-full border-4 border-white w-32 h-32 flex items-center justify-center bg-white bg-opacity-30 text-gray-800">
              Upload Image
            </div>
          )}
        </div>
        <div className="flex-grow">
          <h1 className="text-3xl font-bold text-white mb-4">{userName}'s Profile</h1>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-white">
                Upload Image:
                <input
                  type="file"
                  onChange={handleImageChange}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  required={!image}
                />
              </label>
            </div>

            <div>
              <label className="block text-white">
                Area:
                <input
                  type="text"
                  value={area}
                  onChange={(e) => setArea(e.target.value)}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  required
                />
              </label>
            </div>

            <div>
              <label className="block text-white">
                City:
                <input
                  type="text"
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  required
                />
              </label>
            </div>

            <div>
              <label className="block text-white">
                State:
                <input
                  type="text"
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  required
                />
              </label>
            </div>

            <div>
              <label className="block text-white">
                Pincode:
                <input
                  type="text"
                  value={pincode}
                  onChange={(e) => setPincode(e.target.value)}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  required
                />
              </label>
            </div>

            <div>
              <label className="block text-white">
                Bio:
                <textarea
                  value={bio}
                  onChange={(e) => setBio(e.target.value)}
                  className="mt-1 block w-full border rounded p-2 bg-gray-200 text-gray-800"
                  rows="4"
                  placeholder="Tell us about yourself..."
                />
              </label>
            </div>

            <button type="submit" className="mt-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition">
              Update Profile
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TaskerProfile;
