import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PaymentsStatus = () => {
  const [bookings, setBookings] = useState([]);
  const taskerId = sessionStorage.getItem('taskerId'); // Retrieve taskerId from session storage

  useEffect(() => {
    // Fetch bookings based on taskerId
    axios.get(`/api/bookings/${taskerId}`)
      .then(response => setBookings(response.data))
      .catch(error => console.error('Error fetching bookings:', error));
  }, [taskerId]);

  return (
    <div className="container mx-auto px-4 py-6">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Payment Status</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {bookings.map((booking) => (
          <div key={booking.bookingId} className="bg-white shadow-md rounded-lg p-6">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">
              {booking.customerFirstName} {booking.customerLastName}
            </h3>
            <p className="text-gray-600">
              <span className="font-semibold">Total Amount:</span> ₹{booking.total_amount}
            </p>
            <p className="text-gray-600">
              <span className="font-semibold">Paid Amount:</span> ₹{booking.partial_amount}
            </p>
            <p className="text-gray-600">
              <span className="font-semibold">Balance:</span> ₹{booking.balance_amount}
            </p>
            <p className={`mt-4 py-1 px-3 rounded-full text-center 
              ${booking.payment_status === 'Pending' ? 'bg-red-100 text-red-600' :
                booking.payment_status === 'Partially Paid' ? 'bg-yellow-100 text-yellow-600' :
                'bg-green-100 text-green-600'}`}>
              {booking.payment_status}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PaymentsStatus;
