import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  // Retrieve user data from session storage
  const hourlyRate = parseFloat(sessionStorage.getItem('hourlyRate')) || 0;
  const userId = sessionStorage.getItem('userId');
  const serviceId = sessionStorage.getItem('serviceId');

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [hours, setHours] = useState(1);
  const [amount, setAmount] = useState(hourlyRate * hours * 100); // In paise

  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.async = true;
      document.body.appendChild(script);
    };
    loadRazorpayScript();
  }, []);

  const handlePayment = async (e) => {
    e.preventDefault();
    if (!window.Razorpay) {
      alert('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    const totalAmount = hourlyRate * hours * 100;  // Total amount in paise
    const partialAmount = totalAmount / 2;

    const options = {
      key: 'rzp_test_KA0MVYPAHSnecF', // Replace with live key in production
      amount: partialAmount,
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async (response) => {
        try {
          await axios.post('/api/booking/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            userId,
            taskerId,
            serviceId,
            totalAmount,
            partialAmount,
            hours,
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container bg-gray-50 min-h-screen flex justify-center items-center py-10">
      <div className="billing-info bg-white p-8 rounded-lg shadow-lg w-full max-w-4xl grid grid-cols-1 md:grid-cols-2 gap-8">

        {/* Left Side: Form Inputs */}
        <div className="form-container space-y-6">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Billing Information</h2>

          <form onSubmit={handlePayment} className="space-y-6">
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Name"
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="tel"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              placeholder="Contact"
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <input
              type="number"
              value={hours}
              onChange={(e) => setHours(e.target.value)}
              min="1"
              required
              placeholder="Hours"
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
          </form>
        </div>

        {/* Right Side: Checkout Summary and Pay Now Button */}
        <div className="checkout-summary bg-gray-100 p-6 rounded-lg shadow-sm space-y-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">Order Summary</h3>

          <div className="space-y-4">
            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Hourly Rate:</span>
              <span className="font-medium text-gray-900">₹{hourlyRate.toFixed(2)}</span>
            </div>
            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Hours:</span>
              <span className="font-medium text-gray-900">{hours}</span>
            </div>
            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Total Amount:</span>
              <span className="font-medium text-gray-900">₹{(hourlyRate * hours).toFixed(2)}</span>
            </div>
          </div>

          <button
            type="submit"
            onClick={handlePayment}
            className="w-full py-3 px-6 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition duration-300"
          >
            Pay Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default Payment;
