import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Payment = () => {
  const location = useLocation();
  const { taskerId } = location.state || {};

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [contact, setContact] = useState('');
  const [hours, setHours] = useState(1);
  const [amount, setAmount] = useState(50000);

  // Load Razorpay script
  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.async = true;
      script.onload = () => console.log('Razorpay script loaded');
      script.onerror = () => console.log('Failed to load Razorpay script');
      document.body.appendChild(script);
    };

    loadRazorpayScript();
  }, []);

  const handlePayment = async (e) => {
    e.preventDefault();

    // Ensure Razorpay script is loaded
    if (typeof window.Razorpay === 'undefined') {
      alert('Razorpay SDK failed to load. Please check your connection.');
      return;
    }

    const options = {
      key: 'rzp_test_KA0MVYPAHSnecF',
      amount: amount,
      currency: 'INR',
      name: 'Your Company Name',
      description: 'Booking Tasker',
      handler: async function (response) {
        try {
          await axios.post('/api/payment-verification', {
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_order_id: response.razorpay_order_id,
            razorpay_signature: response.razorpay_signature,
            taskerId,
          });
          alert('Payment successful!');
        } catch (error) {
          alert('Payment verification failed.');
        }
      },
      prefill: { name, email, contact },
      notes: { taskerId },
      theme: { color: '#F37254' },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <div className="payment-container">
      <div className="payment-form">
        <div className="billing-info">
          <h2 className="section-title">Billing Information</h2>
          <form onSubmit={handlePayment}>
            <div className="input-group">
              <label>Name</label>
              <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
            <div className="input-group">
              <label>Email</label>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div className="input-group">
              <label>Contact Number</label>
              <input type="tel" value={contact} onChange={(e) => setContact(e.target.value)} required />
            </div>
            <div className="input-group">
              <label>Hours of Work</label>
              <input type="number" value={hours} onChange={(e) => setHours(e.target.value)} min="1" required />
            </div>
          </form>
        </div>

        <div className="checkout-summary">
          <h2 className="section-title">Checkout</h2>
          <div className="summary-details">
            <p><strong>Tasker:</strong> {taskerId ? `Tasker #${taskerId}` : 'Unknown'}</p>
            <p><strong>Hours:</strong> {hours} hr(s)</p>
            <p><strong>Amount:</strong> ₹{amount}</p>
          </div>
          <button onClick={handlePayment} className="payment-button">
            Pay Now
          </button>
        </div>
      </div>

      <style jsx>{`
        .payment-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background-color: #f7f7f7;
          font-family: 'Arial', sans-serif;
        }

        .payment-form {
          display: flex;
          background: #ffffff;
          box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
          border-radius: 8px;
          overflow: hidden;
          width: 80%;
          max-width: 1200px;
          margin: 20px;
        }

        .billing-info {
          padding: 20px;
          flex: 1;
          border-right: 1px solid #ddd;
        }

        .checkout-summary {
          padding: 20px;
          flex: 1;
          background-color: #f9f9f9;
        }

        .section-title {
          font-size: 1.5rem;
          font-weight: bold;
          color: #333;
          margin-bottom: 15px;
        }

        .input-group {
          margin-bottom: 15px;
        }

        .input-group label {
          display: block;
          font-size: 1rem;
          color: #555;
          margin-bottom: 5px;
        }

        .input-group input {
          width: 100%;
          padding: 10px;
          font-size: 1rem;
          border: 1px solid #ccc;
          border-radius: 4px;
        }

        .input-group input:focus {
          border-color: #007BFF;
          outline: none;
        }

        .summary-details p {
          font-size: 1rem;
          color: #333;
          margin-bottom: 10px;
        }

        .payment-button {
          padding: 12px 20px;
          font-size: 1.2rem;
          background-color: #F37254;
          color: white;
          border: none;
          border-radius: 5px;
          width: 100%;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }

        .payment-button:hover {
          background-color: #F15B48;
        }

        @media (max-width: 768px) {
          .payment-form {
            flex-direction: column;
          }

          .billing-info, .checkout-summary {
            border-right: none;
            border-bottom: 1px solid #ddd;
          }

          .payment-button {
            margin-top: 20px;
          }
        }
      `}</style>
    </div>
  );
};

export default Payment;
