import React, { useState } from 'react';

function ProfilePage() {
  const [image, setImage] = useState(null);
  const [address, setAddress] = useState('');
  const userID = localStorage.getItem('userID');  // Get user ID from local storage
  const userName = localStorage.getItem('name'); // Get user name from local storage

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleAddressChange = (event) => {
    setAddress(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('image', image);
    formData.append('address', address);
    formData.append('userID', userID);  // Send userID as well

    try {
      const response = await fetch('/api/tasker-profile', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        // Handle success (e.g., show a success message)
        console.log('Profile updated successfully');
      } else {
        // Handle error (e.g., show an error message)
        console.error('Error updating profile');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800">{userName}'s Tasker Profile</h1>
      <form onSubmit={handleSubmit} className="mt-4 bg-white p-6 rounded-lg shadow-md">
        <div className="mb-4">
          <label className="block text-gray-700">
            Name:
            <input
              type="text"
              value={userName}
              readOnly
              className="mt-1 block w-full border rounded p-2"
            />
          </label>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">
            Upload Image:
            <input
              type="file"
              onChange={handleImageChange}
              className="mt-1 block w-full border rounded p-2"
              required
            />
          </label>
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">
            Address:
            <input
              type="text"
              value={address}
              onChange={handleAddressChange}
              className="mt-1 block w-full border rounded p-2"
              required
            />
          </label>
        </div>
        <button type="submit" className="mt-4 bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
          Submit Profile
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
